var mongoose = require('mongoose');

exports.User = mongoose.model('User', mongoose.Schema({
    username: String,
    password: String,
    fullname: String,
    email: String,
    role: String
})
);



exports.connect = function() {
    if (global.isProd) {
      mongoose.connect(process.env.OPENSHIFT_MONGODB_DB_URL + "mydb");
      //mongoose.connect('mongodb://admin:UB9T1s5251Z4@' + process.env.OPENSHIFT_MONGODB_DB_HOST + ':' + process.env.OPENSHIFT_MONGODB_DB_PORT + '/invest');
    
    } else {
      mongoose.connect('mongodb://localhost/mydb');
      //mongoose.connect('mongodb://localhost/invest');
    }
    
}
